import { useState } from "react";

function Square({ value, onSquareClick, sqClass }) {
  return (
    <button className={sqClass} onClick={onSquareClick}>
      {value}
    </button>
  );
}

export default function Game() {
  const [history, setHistory] = useState([Array(9).fill(null)]);
  const [currentMove, setCurrentMove] = useState(0);
  const xIsNext = currentMove % 2 === 0;
  const currentSquares = history[currentMove];
  let resetStatus = 0;

  function handlePlay(nextSquares) {
    const nextHistory = [...history.slice(0, currentMove + 1), nextSquares]; // The spread (...) syntax allows an iterable, such as an array or string, to be expanded in places where zero or more arguments (for function calls) or elements (for array literals) are expected.
    setHistory(nextHistory);
    setCurrentMove(nextHistory.length - 1);
  }

  let moves = history.map((squares, move) => {
    let description;
    let btnStatus;
    if (move > 0) {
      description = "Go to move #" + move;
    } else {
      description = "Go to game start";
    }
    if(currentMove === move) {
      btnStatus = "active";
    }
    return (
      <li key={move} id={move}>
        <button className={btnStatus} onClick={() => jumpTo(move)}>
          {description}
        </button>
      </li>
    );
  });

  function jumpTo(nextMove) {
    setCurrentMove(nextMove);
  }

  function reset() {
    for (let i = 0; i < currentSquares.length; i++) {
      currentSquares[i] = null;
    }
    setCurrentMove(0);
    resetStatus = 1;
    jumpTo(0);
    resetHistory(setHistory);
  }

  return (
    <>
      <div className="game-board">
        <Board xIsNext={xIsNext} squares={currentSquares} onPlay={handlePlay} />
      </div>
      <div className="game-actions">
        <div className="game-plays">
          <ul>{moves}</ul>
        </div>
        <div>
          <button onClick={() => reset()}>Reset</button>
        </div>
      </div>
    </>
  );
}
function removeGameSteps(el, arr, curStep) {
  const prevHistory = arr.slice(0, curStep);
  el(prevHistory);
}
function resetHistory(el) {
  el([Array(9).fill(null)]);
}

function Board({ xIsNext, squares, onPlay, resetStatus }) {
  function handleClick(i) {
    // If squares already has a value to it (X or O) it'll return early before updating the copied squares list.
    if (squares[i] || calculateWinner(squares)) {
      return;
    }

    // Creating a copy of squares for the sake of immutability.
    // Immutability makes complex features much easier to implement and makes it very cheap for components to compare whether their data has changed or not.
    const nextSquares = squares.slice();
    xIsNext ? (nextSquares[i] = "X") : (nextSquares[i] = "O");
    onPlay(nextSquares);
  }

  const winner = calculateWinner(squares);
  let status;
  let sqStatus = 'square';

  if (winner || resetStatus === 0) {
    status = "Winner: " + winner;
  } else {
    status = "Next player: " + (xIsNext ? "X" : "O");
  }

  return (
    <>
      <div id="game-table">
        <Square sqClass={sqStatus} value={squares[0]} onSquareClick={() => handleClick(0)} />
        <Square sqClass={sqStatus} value={squares[1]} onSquareClick={() => handleClick(1)} />
        <Square sqClass={sqStatus} value={squares[2]} onSquareClick={() => handleClick(2)} />
        <Square sqClass={sqStatus} value={squares[3]} onSquareClick={() => handleClick(3)} />
        <Square sqClass={sqStatus} value={squares[4]} onSquareClick={() => handleClick(4)} />
        <Square sqClass={sqStatus} value={squares[5]} onSquareClick={() => handleClick(5)} />
        <Square sqClass={sqStatus} value={squares[6]} onSquareClick={() => handleClick(6)} />
        <Square sqClass={sqStatus} value={squares[7]} onSquareClick={() => handleClick(7)} />
        <Square sqClass={sqStatus} value={squares[8]} onSquareClick={() => handleClick(8)} />
      </div>
      <div className="status">{status}</div>
    </>
  );
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [2, 4, 6],
    [0, 4, 8],
  ];
  for (let i = 0; i < lines.length; i++) {
    // Destructuring assignment
    const [a, b, c] = lines[i]; // The destructuring assignment syntax is a JavaScript expression that makes it possible to unpack values from arrays, or properties from objects, into distinct variables.

    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      console.log("We have a winner! It's " + squares[a]);
      sqStatus = 'square active';
      return squares[a];
    }
  }
  return null;
}
